              <div>
                <h2 className="text-2xl font-bold text-gray-900 dark:text-white">
                  {lang === 'ar' ? 'Ø·Ù„Ø¨ Ø®Ø¯Ù…Ø© ØªØ³ÙˆÙŠÙ‚ÙŠØ©' : 'Marketing Service Request'}
                </h2>
                <p className="text-gray-600 dark:text-gray-400">
                  {stepTitles[currentStep as keyof typeof stepTitles]}
                </p>
              </div>
            </div>

            <button
              onClick={onClose}
              className="text-gray-400 hover:text-gray-600 dark:hover:text-gray-300 transition-colors"
              data-testid="button-close-wizard"
            >
              Ã—
            </button>
          </div>

          {/* Progress */}
          <div className="space-y-2">
            <div className="flex justify-between text-sm text-gray-600 dark:text-gray-400">
              <span>{lang === 'ar' ? `Ø§Ù„Ø®Ø·ÙˆØ© ${currentStep} Ù…Ù† ${totalSteps}` : `Step ${currentStep} of ${totalSteps}`}</span>
              <span>{Math.round(progressPercentage)}%</span>
            </div>
            <Progress value={progressPercentage} className="h-2" />
          </div>
        </div>

        {/* Content */}
        <div className="p-6 overflow-y-auto max-h-[calc(90vh-200px)]">
          <AnimatePresence mode="wait">
            {currentStep === 1 && (
              <PackageSelection
                selectedPackage={selectedPackage}
                onSelectPackage={setSelectedPackage}
                onNext={handleNext}
              />
            )}

            {currentStep === 2 && selectedPackage && (
              <FeatureSelection
                selectedPackage={selectedPackage}
                selectedFeatures={selectedFeatures}
                onFeaturesChange={setSelectedFeatures}
                onNext={handleNext}
                onBack={handleBack}
              />
            )}

            {currentStep === 3 && (
              <ContactStep
                formData={formData}
                onFormDataChange={setFormData}
                attachedFiles={attachedFiles}
                onFilesChange={setAttachedFiles}
                onNext={handleNext}
                onBack={handleBack}
              />
            )}

            {currentStep === 4 && (
              <SummaryStep
                formData={formData}
                selectedPackage={selectedPackage}
                selectedFeatures={selectedFeatures}
                attachedFiles={attachedFiles}
                featurePrices={featurePrices}
                onBack={handleBack}
                onSubmit={handleSubmit}
                isSubmitting={submitRequestMutation.isPending}
              />
            )}
          </AnimatePresence>
        </div>

        {/* Loading State */}
        {submitRequestMutation.isPending && (
          <div className="absolute inset-0 bg-white/80 dark:bg-gray-900/80 flex items-center justify-center">
            <div className="text-center">
              <div className="w-8 h-8 border-4 border-orange-200 border-t-orange-600 rounded-full animate-spin mx-auto mb-4"></div>
              <p className="text-gray-600 dark:text-gray-400">
                {lang === 'ar' ? 'Ø¬Ø§Ø±ÙŠ Ø¥Ø±Ø³Ø§Ù„ Ø§Ù„Ø·Ù„Ø¨...' : 'Submitting request...'}
              </p>
            </div>
          </div>
        )}
      </motion.div>
    </div>
  );
}

