interface AuditLogEntry {
  id: string;
  actorId: string;
  action: string;
  entityType: string;
  entityId: string;
  entityName?: string;
  diff?: {
    before?: Record<string, any>;
    after?: Record<string, any>;
    changed?: string[];
  };
  metadata?: {
    userAgent?: string;
    ipAddress?: string;
    source?: string;
    requestId?: string;
    sessionId?: string;
  };
  createdAt: string;
  actorName?: string; // Will be populated from user lookup
}

interface AuditHistoryTabProps {
  entityType: string;
  entityId: string;
  entityName?: string;
  className?: string;
}

export const AuditHistoryTab: React.FC<AuditHistoryTabProps> = ({
  entityType,
  entityId,
  entityName,
  className = ''
}) => {
  const [auditLogs, setAuditLogs] = useState<AuditLogEntry[]>([]);
  const [loading, setLoading] = useState(true);
  const [page, setPage] = useState(1);
  const [totalPages, setTotalPages] = useState(1);
  const [expandedLog, setExpandedLog] = useState<string | null>(null);

  useEffect(() => {
    loadAuditLogs();
  }, [entityType, entityId, page]);

  const loadAuditLogs = async () => {
    try {
      setLoading(true);
      const response = await fetch(
        `/api/audit-logs?entityType=${entityType}&entityId=${entityId}&page=${page}&limit=20`
      );

      if (response.ok) {
        const data = await response.json();
        setAuditLogs(data.logs);
        setTotalPages(data.totalPages);
      }
    } catch (error) {
      console.error('Failed to load audit logs:', error);
    } finally {
      setLoading(false);
    }
  };

