    switch (action.toLowerCase()) {
      case 'create':
        return 'success';
      case 'update':
      case 'edit':
        return 'default';
      case 'delete':
        return 'danger';
      case 'export':
        return 'warning';
      case 'view':
        return 'secondary';
      case 'assign':
      case 'escalate':
        return 'warning';
      default:
        return 'default';
    }
