import { motion } from "framer-motion";
import { Button } from "@/components/ui/button";
import { useLanguage } from "@/i18n/lang";
import { useTranslation } from "@/hooks/useTranslation";
import { cn } from "@/lib/utils";
import { AlertTriangle, CheckCircle, Play } from "lucide-react";

export function ProblemSolution() {
  const { dir } = useLanguage();
  const { t } = useTranslation();

  // Define problem and solution points directly with language support
  const problemPoints = [
    dir === 'rtl' ? "Ø£Ø¯ÙˆØ§Øª Ù…ØªÙ†Ø§Ø«Ø±Ø© ÙˆÙ…Ù†ÙØµÙ„Ø©" : "Fragmented and disconnected tools",
    dir === 'rtl' ? "Ø¹Ø¯Ù… Ù…ØªØ§Ø¨Ø¹Ø© Ø§Ù„Ø¹Ù…Ù„Ø§Ø¡ Ø¨Ø´ÙƒÙ„ ÙØ¹Ø§Ù„" : "Poor customer follow-up",
    dir === 'rtl' ? "ÙÙˆØ¶Ù‰ ÙÙŠ Ø¥Ø¯Ø§Ø±Ø© Ø§Ù„Ø¨ÙŠØ§Ù†Ø§Øª" : "Data chaos and disorganization",
    dir === 'rtl' ? "ØµØ¹ÙˆØ¨Ø© ÙÙŠ ØªØªØ¨Ø¹ Ø§Ù„Ø£Ø¯Ø§Ø¡" : "Difficulty tracking performance"
  ];

  const solutionPoints = [
    dir === 'rtl' ? "Ù†Ø¸Ø§Ù… CRM Ù…ÙˆØ­Ø¯ ÙˆÙ…ØªÙƒØ§Ù…Ù„" : "Unified and integrated CRM system",
    dir === 'rtl' ? "ØªÙ†Ø¨ÙŠÙ‡Ø§Øª ØªÙ„Ù‚Ø§Ø¦ÙŠØ© Ù„Ù„Ù…ØªØ§Ø¨Ø¹Ø©" : "Automated follow-up alerts",
    dir === 'rtl' ? "ØªØ­Ù„ÙŠÙ„Ø§Øª Ø´Ø§Ù…Ù„Ø© ÙˆÙ…Ø±ÙƒØ²Ø©" : "Consolidated analytics and insights",
    dir === 'rtl' ? "Ù„ÙˆØ­Ø© ØªØ­ÙƒÙ… ÙˆØ§Ø¶Ø­Ø© ÙˆØ³Ù‡Ù„Ø©" : "Clear and easy dashboard"
  ];

  return (
    <section className="py-20 bg-white">
      <div className="container mx-auto px-4 sm:px-6 lg:px-8">
        <div className="grid lg:grid-cols-2 gap-16 items-center">
          {/* Problem Section */}
          <motion.div
            className={cn(
              "space-y-8",
              dir === "rtl" && "lg:order-2"
            )}
            initial={{ opacity: 0, x: dir === 'rtl' ? 50 : -50 }}
            whileInView={{ opacity: 1, x: 0 }}
            transition={{ duration: 0.8 }}
            viewport={{ once: true }}
          >
            <div className="space-y-4">
              <motion.div
                className="inline-flex items-center gap-2 px-4 py-2 bg-red-50 text-red-600 rounded-full text-sm font-medium"
                initial={{ opacity: 0, scale: 0.8 }}
                whileInView={{ opacity: 1, scale: 1 }}
                transition={{ duration: 0.6, delay: 0.2 }}
                viewport={{ once: true }}
              >
                <AlertTriangle className="w-4 h-4" />
                {dir === 'rtl' ? 'Ø§Ù„ØªØ­Ø¯ÙŠØ§Øª Ø§Ù„Ø­Ø§Ù„ÙŠØ©' : 'Current Challenges'}
              </motion.div>

              <motion.h2
                className="text-3xl md:text-4xl font-bold text-brand-text-primary"
                initial={{ opacity: 0, y: 30 }}
                whileInView={{ opacity: 1, y: 0 }}
                transition={{ duration: 0.8, delay: 0.3 }}
                viewport={{ once: true }}
              >
                {t('problem.title')}
              </motion.h2>
            </div>

            <div className="space-y-4">
              {problemPoints.map((point, index) => (
                <motion.div
                  key={index}
                  className="flex items-start gap-3 p-4 bg-red-50 rounded-xl border border-red-100"
