/* Import local Cairo fonts from @fontsource */
@import "@fontsource/cairo/300.css";
@import "@fontsource/cairo/400.css";
@import "@fontsource/cairo/600.css";
@import "@fontsource/cairo/700.css";

@tailwind base;
@tailwind components;
@tailwind utilities;

:root {
  --background: hsl(0, 0%, 100%);
  --foreground: hsl(210, 25%, 7.8431%);
  --card: hsl(180, 6.6667%, 97.0588%);
  --card-foreground: hsl(210, 25%, 7.8431%);
  --popover: hsl(0, 0%, 100%);
  --popover-foreground: hsl(210, 25%, 7.8431%);
  --primary: hsl(213, 94%, 68%); /* GSC Light Blue */
  --primary-foreground: hsl(0, 0%, 100%);
  --primary-dark: hsl(213, 87%, 60%); /* GSC Darker Blue */
  --gsc-blue: hsl(213, 94%, 68%);
  --gsc-light: hsl(213, 100%, 96%);
  --secondary: hsl(210, 25%, 7.8431%);
  --secondary-foreground: hsl(0, 0%, 100%);
  --muted: hsl(240, 1.9608%, 90%);
  --muted-foreground: hsl(210, 25%, 7.8431%);
  --accent: hsl(211.5789, 51.3514%, 92.7451%);
  --accent-foreground: hsl(203.8863, 88.2845%, 53.1373%);
  --destructive: hsl(356.3033, 90.5579%, 54.3137%);
  --destructive-foreground: hsl(0, 0%, 100%);
  --border: hsl(201.4286, 30.4348%, 90.9804%);
  --input: hsl(200, 23.0769%, 97.4510%);
  --ring: hsl(202.8169, 89.1213%, 53.1373%);
  --light-gray: hsl(210, 16.6667%, 97.6471%);
  --font-cairo: 'Cairo', sans-serif;
  --radius: 1.3rem;

  /* Brand Colors (White + Sky Blue) */
  --brand-bg: hsl(0, 0%, 100%);
  --brand-sky-light: hsl(206, 100%, 95%);
  --brand-sky-base: hsl(204, 100%, 90%);
  --brand-sky-accent: hsl(200, 100%, 73%);
  --brand-text-primary: hsl(220, 35%, 9%);
  --brand-text-muted: hsl(220, 14%, 37%);
}

.dark {
  --background: hsl(0, 0%, 0%);
  --foreground: hsl(200, 6.6667%, 91.1765%);
  --card: hsl(228, 9.8039%, 10%);
  --card-foreground: hsl(0, 0%, 85.0980%);
  --popover: hsl(0, 0%, 0%);
  --popover-foreground: hsl(200, 6.6667%, 91.1765%);
  --primary: hsl(203.7736, 87.6033%, 52.5490%);
  --primary-foreground: hsl(0, 0%, 100%);
  --secondary: hsl(195.0000, 15.3846%, 94.9020%);
  --secondary-foreground: hsl(210, 25%, 7.8431%);
  --muted: hsl(0, 0%, 9.4118%);
  --muted-foreground: hsl(210, 3.3898%, 46.2745%);
  --accent: hsl(205.7143, 70%, 7.8431%);
  --accent-foreground: hsl(203.7736, 87.6033%, 52.5490%);
  --destructive: hsl(356.3033, 90.5579%, 54.3137%);
  --destructive-foreground: hsl(0, 0%, 100%);
  --border: hsl(210, 5.2632%, 14.9020%);
  --input: hsl(207.6923, 27.6596%, 18.4314%);
  --ring: hsl(202.8169, 89.1213%, 53.1373%);
}

@layer base {
  * {
    @apply border-border;
  }

  body {
    @apply bg-background text-foreground;
    font-family: var(--font-cairo);
    direction: rtl;
  }

  html {
    scroll-behavior: smooth;
  }

  /* Enhanced scroll effects and animations */
  body {
    overflow-x: hidden;
  }

  /* Scroll reveal animations */
  @keyframes scrollReveal {
    from {
      opacity: 0;
      transform: translateY(50px);
    }
    to {
      opacity: 1;
      transform: translateY(0);
    }
  }

  @keyframes slideInLeft {
    from {
      opacity: 0;
      transform: translateX(-100px);
    }
    to {
      opacity: 1;
      transform: translateX(0);
    }
  }

  @keyframes slideInRight {
    from {
      opacity: 0;
      transform: translateX(100px);
    }
    to {
      opacity: 1;
      transform: translateX(0);
    }
  }

  @keyframes scaleIn {
    from {
      opacity: 0;
      transform: scale(0.8);
    }
    to {
      opacity: 1;
      transform: scale(1);
    }
  }

  /* Floating animation for decorative elements */
  @keyframes float {
    0%, 100% {
      transform: translateY(0px) rotate(0deg);
    }
    50% {
      transform: translateY(-20px) rotate(180deg);
    }
  }

  @keyframes floatReverse {
    0%, 100% {
      transform: translateY(0px) rotate(0deg);
    }
    50% {
      transform: translateY(20px) rotate(-180deg);
    }
  }

  /* Parallax container */
  .parallax-container {
    transform-style: preserve-3d;
    perspective: 1000px;
  }

  /* Smooth transitions for all interactive elements */
  * {
    transition: transform 0.3s ease, opacity 0.3s ease;
  }

  /* Enhanced hover effects */
  .hover-lift:hover {
    transform: translateY(-8px) scale(1.02);
    box-shadow: 0 20px 40px rgba(0, 0, 0, 0.1);
  }

  .hover-glow:hover {
    box-shadow: 0 0 30px rgba(var(--primary-rgb), 0.3);
  }

  /* Scroll indicator enhancements */
  .scroll-indicator {
    background: linear-gradient(90deg, var(--primary), var(--primary-dark));
    animation: shimmer 2s ease-in-out infinite;
  }

  @keyframes shimmer {
    0%, 100% {
      opacity: 0.8;
    }
    50% {
      opacity: 1;
    }
  }
}

@layer components {
  .btn-primary {
    @apply bg-primary hover:bg-primary-dark text-white px-8 py-4 rounded-lg transition-all duration-300 font-medium shadow-lg hover:shadow-xl;
  }

  .btn-secondary {
    @apply border-2 border-primary text-primary hover:bg-primary hover:text-white px-8 py-4 rounded-lg transition-all duration-300 font-medium;
  }

  .card-hover {
    @apply hover:shadow-xl transition-all duration-300 border border-gray-100 hover:border-primary;
  }

  .gradient-primary {
    background: linear-gradient(to bottom right, var(--primary), var(--primary-dark));
  }

  .gradient-gsc {
    background: linear-gradient(135deg, hsl(213, 94%, 68%) 0%, hsl(213, 87%, 60%) 100%);
  }

  .bg-gsc-light {
    background-color: hsl(213, 100%, 96%);
  }

  .gradient-light {
    background: linear-gradient(to bottom right, var(--light-gray), var(--background));
  }

  .text-gradient {
    background: linear-gradient(45deg, var(--primary), var(--primary-dark));
    -webkit-background-clip: text;
    -webkit-text-fill-color: transparent;
    background-clip: text;
  }
}

@layer utilities {
  .animation-delay-200 {
    animation-delay: 200ms;
  }

  .animation-delay-400 {
    animation-delay: 400ms;
  }

  .animation-delay-600 {
    animation-delay: 600ms;
  }

  .animation-delay-800 {
    animation-delay: 800ms;
  }

  .animation-delay-1000 {
    animation-delay: 1000ms;
  }

  .bg-300\% {
    background-size: 300% 300%;
  }
}

/* RTL specific styles */
[dir="rtl"] .space-x-reverse > :not([hidden]) ~ :not([hidden]) {
  --tw-space-x-reverse: 1;
}

/* Portfolio filter animations */
.portfolio-item {
  transition: all 0.3s ease;
}

.portfolio-item.hidden {
  opacity: 0;
  transform: scale(0.8);
}

/* Text clamp utilities */
.line-clamp-1 {
  overflow: hidden;
  display: -webkit-box;
  -webkit-box-orient: vertical;
  -webkit-line-clamp: 1;
}

.line-clamp-2 {
  overflow: hidden;
  display: -webkit-box;
  -webkit-box-orient: vertical;
  -webkit-line-clamp: 2;
}

.line-clamp-3 {
  overflow: hidden;
  display: -webkit-box;
  -webkit-box-orient: vertical;
  -webkit-line-clamp: 3;
}

/* Smooth scrolling enhancement */
@media (prefers-reduced-motion: no-preference) {
  .scroll-smooth {
    scroll-behavior: smooth;
  }
}

/* Enhanced animations for better performance */
@media (prefers-reduced-motion: no-preference) {
  .floating-animation {
    animation: float 6s ease-in-out infinite;
  }

  .pulse-animation {
    animation: pulse 4s ease-in-out infinite;
  }

  @keyframes float {
    0%, 100% { transform: translateY(0px); }
    50% { transform: translateY(-20px); }
  }

  @keyframes pulse {
    0%, 100% { transform: scale(1); }
    50% { transform: scale(1.05); }
  }
}

/* Reduced motion preferences */
@media (prefers-reduced-motion: reduce) {
  * {
    animation-duration: 0.01ms !important;
    animation-iteration-count: 1 !important;
    transition-duration: 0.01ms !important;
  }
}

