    height: '2.5rem',   // h-10 (40px)
    padding: '0.75rem', // px-3
  },
  card: {
    padding: '1.5rem',  // p-6 (24px)
  },
  table: {
    cellPadding: '1rem', // p-4 (16px)
    headerHeight: '3rem', // h-12 (48px)
  },
} as const;

// Export all tokens as a single object for easy importing
export const tokens = {
  spacing,
  radius,
  shadows,
  fontSizes,
  fontWeights,
  zIndex,
  containers,
  durations,
