export const checkAndMarkSLABreaches = (): void => {
  const now = new Date();
  let hasChanges = false;

  crmRequests.forEach(request => {
    if (request.sla && !request.sla.breached && !request.sla.paused) {
      const dueAt = new Date(request.sla.dueAt);
      if (now > dueAt) {
