    const shareUrl = platform.getUrl(url, title, description);
    window.open(shareUrl, "_blank", "width=600,height=400");

    // Track sharing event (you can integrate with analytics)
    toast({
      title: "ØªÙ… Ø§Ù„Ù…Ø´Ø§Ø±ÙƒØ©!",
      description: `ØªÙ… Ù…Ø´Ø§Ø±ÙƒØ© Ø§Ù„Ù…Ø­ØªÙˆÙ‰ Ø¹Ù„Ù‰ ${platform.name}`,
    });
  };

  const copyToClipboard = async () => {
    try {
      await navigator.clipboard.writeText(url);
      toast({
        title: "ØªÙ… Ø§Ù„Ù†Ø³Ø®!",
        description: "ØªÙ… Ù†Ø³Ø® Ø§Ù„Ø±Ø§Ø¨Ø· Ø¥Ù„Ù‰ Ø§Ù„Ø­Ø§ÙØ¸Ø©",
      });
    } catch (err) {
      toast({
        title: "Ø®Ø·Ø£",
        description: "ÙØ´Ù„ ÙÙŠ Ù†Ø³Ø® Ø§Ù„Ø±Ø§Ø¨Ø·",
        variant: "destructive",
      });
    }
  };

  return (
    <div className={`relative ${className}`}>
      <motion.div
        initial={false}
        animate={{ scale: isOpen ? 1.1 : 1 }}
        className="flex items-center gap-2"
      >
        {/* Share button */}
        <Button
          onClick={() => setIsOpen(!isOpen)}
          variant="outline"
          size="sm"
          className="relative"
        >
          <i className="fas fa-share-alt mr-2"></i>
          {showLabels && "Ù…Ø´Ø§Ø±ÙƒØ©"}
        </Button>

        {/* Copy link button */}
        <Button
          onClick={copyToClipboard}
          variant="ghost"
          size="sm"
        >
          <i className="fas fa-copy"></i>
        </Button>
      </motion.div>

      {/* Social platforms */}
      <motion.div
        initial={{ opacity: 0, scale: 0.8 }}
        animate={{ 
          opacity: isOpen ? 1 : 0,
          scale: isOpen ? 1 : 0.8,
          y: isOpen ? 0 : -10,
        }}
        transition={{ duration: 0.2 }}
        className={`absolute top-full mt-2 flex gap-2 z-20 ${
          isOpen ? "pointer-events-auto" : "pointer-events-none"
        }`}
      >
        {socialPlatforms.map((platform, index) => (
          <motion.div
            key={platform.name}
            initial={{ opacity: 0, y: 10 }}
            animate={{ 
              opacity: isOpen ? 1 : 0,
              y: isOpen ? 0 : 10,
            }}
            transition={{ duration: 0.2, delay: index * 0.05 }}
          >
            <Button
              onClick={() => handleShare(platform)}
              className={`${platform.color} text-white ${sizeClasses[size]} p-0 rounded-full`}
              title={`Ù…Ø´Ø§Ø±ÙƒØ© Ø¹Ù„Ù‰ ${platform.name}`}
            >
              <i className={platform.icon}></i>
            </Button>
          </motion.div>
        ))}
      </motion.div>

      {/* Backdrop */}
      {isOpen && (
        <div
          className="fixed inset-0 z-10"
          onClick={() => setIsOpen(false)}
        />
      )}
    </div>
  );
}

// Floating share button for pages
export function FloatingShareButton(props: SocialShareProps) {
  return (
    <motion.div
      initial={{ opacity: 0, x: 100 }}
      animate={{ opacity: 1, x: 0 }}
      transition={{ duration: 0.5, delay: 1 }}
      className="fixed right-4 top-1/2 -translate-y-1/2 z-40"
    >
      <SocialShare {...props} />
    </motion.div>
  );
}

