// Payment form schema
const paymentSchema = z.object({
  planId: z.string().min(1, "Ù…Ø¹Ø±Ù Ø§Ù„Ø¨Ø§Ù‚Ø© Ù…Ø·Ù„ÙˆØ¨"),
  amount: z.string().min(1, "Ø§Ù„Ù…Ø¨Ù„Øº Ù…Ø·Ù„ÙˆØ¨"),
