scripts/createAdmin.ts.content:
#!/usr/bin/env tsx

import { Pool } from 'pg';
import bcrypt from 'bcrypt';
import { randomUUID } from 'crypto';
import chalk from 'chalk';
import dotenv from 'dotenv';

dotenv.config();

interface AdminData {
  email: string;
  password: string;
  name?: string;
}

async function createSecureAdmin(): Promise<void> {
  console.log(chalk.bold.blue('\nğŸ” GSC Secure Admin Creation\n'));

  // Get admin details from environment or CLI args
  const adminEmail = process.env.ADMIN_EMAIL || process.argv[2];
  const adminPassword = process.env.ADMIN_PASSWORD || process.argv[3];
  const adminName = process.env.ADMIN_NAME || process.argv[4] || 'System Administrator';

  if (!adminEmail || !adminPassword) {
    console.error(chalk.red('âŒ Missing required parameters'));
    console.log(chalk.yellow('Usage: tsx scripts/createAdmin.ts <email> <password> [name]'));
    console.log(chalk.yellow('Or set environment variables: ADMIN_EMAIL, ADMIN_PASSWORD, ADMIN_NAME'));
    process.exit(1);
  }

  // Validate email format
  const emailRegex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
  if (!emailRegex.test(adminEmail)) {
    console.error(chalk.red('âŒ Invalid email format'));
    process.exit(1);
  }

  // Validate password strength
  if (adminPassword.length < 8) {
    console.error(chalk.red('âŒ Password must be at least 8 characters long'));
    process.exit(1);
  }

  if (!process.env.DATABASE_URL) {
    console.error(chalk.red('âŒ DATABASE_URL environment variable is required'));
    process.exit(1);
  }

  const pool = new Pool({ connectionString: process.env.DATABASE_URL });

  try {
    const client = await pool.connect();

    // Check if admin with this email already exists
    const existingAdmin = await client.query(
      'SELECT id, email, role FROM users WHERE email = $1',
      [adminEmail]
    );

    if (existingAdmin.rows.length > 0) {
      const existing = existingAdmin.rows[0];
      if (existing.role === 'admin') {
        console.log(chalk.yellow(`âš ï¸  Admin with email ${adminEmail} already exists`));
        console.log(chalk.blue('Do you want to update the password? (y/N)'));

        // For now, exit - in production you might want to add interactive prompt
        console.log(chalk.red('Exiting to prevent accidental overwrites'));
        process.exit(1);
      } else {
        console.log(chalk.yellow(`âš ï¸  User with email ${adminEmail} exists but is not admin (role: ${existing.role})`));
        console.log(chalk.blue('Updating role to admin...'));
      }
    }

    // Hash the password securely
    console.log(chalk.blue('ğŸ”’ Hashing password...'));
    const saltRounds = 12; // High security for admin accounts
    const hashedPassword = await bcrypt.hash(adminPassword, saltRounds);

    const adminId = randomUUID();
    const now = new Date();

    if (existingAdmin.rows.length > 0) {
      // Update existing user to admin
      await client.query(`
        UPDATE users 
        SET password = $1, role = 'admin', name = $2, updated_at = $3
        WHERE email = $4
      `, [hashedPassword, adminName, now, adminEmail]);

      console.log(chalk.green('âœ… Existing user updated to admin successfully'));
    } else {
      // Create new admin user
      await client.query(`
        INSERT INTO users (id, username, email, password, role, name, is_active, created_at, updated_at)
        VALUES ($1, $2, $3, $4, 'admin', $5, 'true', $6, $7)
      `, [adminId, adminEmail, adminEmail, hashedPassword, adminName, now, now]);

      console.log(chalk.green('âœ… Admin user created successfully'));
    }

    // Create initial admin notification
    const notificationId = randomUUID();
    await client.query(`
      INSERT INTO notifications (id, user_id, title, message, type, category, created_at)
      VALUES ($1, (SELECT id FROM users WHERE email = $2), $3, $4, 'general', 'system', $5)
    `, [
      notificationId,
      adminEmail,
      'Welcome to GSC Admin Panel',
      'Your admin account has been created. Please log in and change your password immediately.',
      now
    ]);

    client.release();
    await pool.end();

    // Print success message and next steps
    console.log(chalk.bold.green('\nğŸ‰ SUCCESS!\n'));
    console.log(chalk.blue('Admin account details:'));
    console.log(chalk.white(`  Email: ${adminEmail}`));
    console.log(chalk.white(`  Name: ${adminName}`));
    console.log(chalk.white(`  Role: admin`));
    console.log(chalk.white(`  Created: ${now.toISOString()}`));

    console.log(chalk.bold.yellow('\nâš¡ IMPORTANT NEXT STEPS:\n'));
    console.log(chalk.yellow('1. Log in to the admin panel immediately'));
    console.log(chalk.yellow('2. Change the password through the settings'));
    console.log(chalk.yellow('3. Enable two-factor authentication if available'));
    console.log(chalk.yellow('4. Review user permissions and access levels'));
    console.log(chalk.yellow('5. Set up monitoring and audit logging'));

    console.log(chalk.bold.red('\nğŸš¨ SECURITY REMINDER:\n'));
    console.log(chalk.red('â€¢ Never store admin credentials in plaintext'));
    console.log(chalk.red('â€¢ Use strong, unique passwords for all admin accounts'));
    console.log(chalk.red('â€¢ Regularly rotate admin passwords'));
    console.log(chalk.red('â€¢ Monitor admin account activity'));

  } catch (error: any) {
    console.error(chalk.red(`âŒ Failed to create admin: ${error.message}`));
    await pool.end();
    process.exit(1);
  }
}

// Handle CLI execution
if (require.main === module) {
  createSecureAdmin().catch((error) => {
    console.error(chalk.red(`Admin creation failed: ${error.message}`));
    process.exit(1);
  });
}

server/security/password.ts.content:
import argon2 from 'argon2';

// Password policy configuration
export const PASSWORD_POLICY = {
  minLength: 8,
  requireUppercase: true,
  requireLowercase: true,
  requireNumbers: true,
  requireSpecialChars: true,
  bannedPatterns: [
    /password/i,
    /123456/,
    /qwerty/i,
    /admin/i,
    /letmein/i,
  ],
};

// Password validation errors
export class PasswordValidationError extends Error {
  constructor(message: string, public suggestions: string[] = []) {
    super(message);
    this.name = 'PasswordValidationError';
  }
}

/**
 * Validates password against security policy
 */
export function validatePassword(password: string): void {
  const errors: string[] = [];
  const suggestions: string[] = [];

  if (password.length < PASSWORD_POLICY.minLength) {
    errors.push(`Password must be at least ${PASSWORD_POLICY.minLength} characters long`);
    suggestions.push(`Use at least ${PASSWORD_POLICY.minLength} characters`);
  }

  if (PASSWORD_POLICY.requireUppercase && !/[A-Z]/.test(password)) {
    errors.push('Password must contain at least one uppercase letter');
    suggestions.push('Add an uppercase letter (A-Z)');
  }

  if (PASSWORD_POLICY.requireLowercase && !/[a-z]/.test(password)) {
    errors.push('Password must contain at least one lowercase letter');
    suggestions.push('Add a lowercase letter (a-z)');
  }

  if (PASSWORD_POLICY.requireNumbers && !/\d/.test(password)) {
    errors.push('Password must contain at least one number');
    suggestions.push('Add a number (0-9)');
  }

  if (PASSWORD_POLICY.requireSpecialChars && !/[!@#$%^&*()_+\-=\[\]{};':"\\|,.<>\/?]/.test(password)) {
    errors.push('Password must contain at least one special character');
    suggestions.push('Add a special character (!@#$%^&*()_+-=[]{}|;\':",./<>?)');
  }

  // Check for banned patterns
  for (const pattern of PASSWORD_POLICY.bannedPatterns) {
    if (pattern.test(password)) {
      errors.push('Password contains common patterns or words');
      suggestions.push('Avoid common words like "password", "admin", or sequential numbers');
      break;
    }
  }

  if (errors.length > 0) {
    const message = errors.join('. ') + '.';
    const exampleSuggestions = [
      'Example strong passwords:',
      'â€¢ MyS3cur3P@ssw0rd!',
      'â€¢ Tr0ub4dor&3',
      'â€¢ C0mplex!Pa$$w0rd2024'
    ];
    throw new PasswordValidationError(message, [...suggestions, ...exampleSuggestions]);
  }
}

/**
 * Hashes a password using Argon2id
 */
export async function hashPassword(password: string): Promise<string> {
  validatePassword(password);

  return await argon2.hash(password, {
    type: argon2.argon2id,
    memoryCost: 2 ** 16, // 64 MB
    timeCost: 3,
    parallelism: 1,
  });
}

/**
 * Verifies a password against its hash
 */
export async function verifyPassword(password: string, hash: string): Promise<boolean> {
  try {
    return await argon2.verify(hash, password);
  } catch (error) {
    // If verification fails due to hash format issues, return false
    return false;
  }
}

/**
 * Generates a strong random password
 */
export function generateSecurePassword(length: number = 16): string {
  const lowercase = 'abcdefghijklmnopqrstuvwxyz';
  const uppercase = 'ABCDEFGHIJKLMNOPQRSTUVWXYZ';
  const numbers = '0123456789';
  const symbols = '!@#$%^&*()_+-=[]{}|;:,.<>?';

  const allChars = lowercase + uppercase + numbers + symbols;

  let password = '';

  // Ensure at least one character from each required category
  password += lowercase[Math.floor(Math.random() * lowercase.length)];
  password += uppercase[Math.floor(Math.random() * uppercase.length)];
  password += numbers[Math.floor(Math.random() * numbers.length)];
  password += symbols[Math.floor(Math.random() * symbols.length)];

  // Fill the rest randomly
  for (let i = 4; i < length; i++) {
    password += allChars[Math.floor(Math.random() * allChars.length)];
  }

  // Shuffle the password
  return password.split('').sort(() => Math.random() - 0.5).join('');
}

scripts/reset.ts.content:
#!/usr/bin/env tsx
import { drizzle } from "drizzle-orm/node-postgres";
import { Pool } from "pg";
import { sql } from "drizzle-orm";

if (!process.env.DATABASE_URL) {
  throw new Error("DATABASE_URL environment variable is required");
}

const pool = new Pool({
  connectionString: process.env.DATABASE_URL,
});

const db = drizzle(pool);

async function resetDatabase() {
  console.log("ğŸ”„ Resetting database...");

  try {
    // Drop all tables in correct order (considering foreign key constraints)
    const dropQueries = [
      'DROP TABLE IF EXISTS "crm_activities" CASCADE;',
      'DROP TABLE IF EXISTS "tasks" CASCADE;',
      'DROP TABLE IF EXISTS "support_tickets" CASCADE;',
      'DROP TABLE IF EXISTS "ticket_messages" CASCADE;',
      'DROP TABLE IF EXISTS "opportunities" CASCADE;',
      'DROP TABLE IF EXISTS "contacts" CASCADE;',
      'DROP TABLE IF EXISTS "accounts" CASCADE;',
      'DROP TABLE IF EXISTS "leads" CASCADE;',
      'DROP TABLE IF EXISTS "deal_stages" CASCADE;',
      'DROP TABLE IF EXISTS "ticket_status" CASCADE;',
      'DROP TABLE IF EXISTS "audit_logs" CASCADE;',
      'DROP TABLE IF EXISTS "saved_filters" CASCADE;',
      'DROP TABLE IF EXISTS "client_requests" CASCADE;',
      'DROP TABLE IF EXISTS "invoices" CASCADE;',
      'DROP TABLE IF EXISTS "notifications" CASCADE;',
      'DROP TABLE IF EXISTS "blog_posts" CASCADE;',
      'DROP TABLE IF EXISTS "projects" CASCADE;',
      'DROP TABLE IF EXISTS "service_requests" CASCADE;',
      'DROP TABLE IF EXISTS "user_subscriptions" CASCADE;',
      'DROP TABLE IF EXISTS "subscription_plans" CASCADE;',
      'DROP TABLE IF EXISTS "testimonials" CASCADE;',
      'DROP TABLE IF EXISTS "services" CASCADE;',
      'DROP TABLE IF EXISTS "portfolio_items" CASCADE;',
      'DROP TABLE IF EXISTS "contact_submissions" CASCADE;',
      'DROP TABLE IF EXISTS "users" CASCADE;',

      // Drop any remaining sequences or functions
      'DROP FUNCTION IF EXISTS update_updated_at_column() CASCADE;'
    ];

    for (const query of dropQueries) {
      try {
        await db.execute(sql.raw(query));
        console.log(`âœ… Executed: ${query}`);
      } catch (error) {
        console.log(`âš ï¸  Warning: ${query} - ${error.message}`);
      }
    }

    console.log("âœ… Database reset completed!");

  } catch (error) {
    console.error("âŒ Reset failed:", error);
    throw error;
  } finally {
    await pool.end();
  }
}

resetDatabase().catch((error) => {
  console.error("âŒ Reset script failed:", error);
  process.exit(1);
});

