import { useState } from 'react';
import { motion, AnimatePresence } from 'framer-motion';
import { 
  X, 
  ChevronLeft, 
  ChevronRight, 
  ZoomIn, 
  ZoomOut, 
  Download,
  Play,
  Pause
} from 'lucide-react';
import { Button } from '@/components/ui/button';

interface GalleryItem {
  id: string;
  url: string;
  alt: string;
  type: 'image' | 'video';
}

interface LightboxGalleryProps {
  items: GalleryItem[];
  initialIndex?: number;
  isOpen: boolean;
  onClose: () => void;
}

export default function LightboxGallery({
  items,
  initialIndex = 0,
  isOpen,
  onClose
}: LightboxGalleryProps) {
  const [currentIndex, setCurrentIndex] = useState(initialIndex);
  const [scale, setScale] = useState(1);
  const [position, setPosition] = useState({ x: 0, y: 0 });
  const [isDragging, setIsDragging] = useState(false);
  const [isVideoPlaying, setIsVideoPlaying] = useState(false);

  const currentItem = items[currentIndex];

  const nextImage = () => {
    setCurrentIndex((prev) => (prev + 1) % items.length);
    resetZoom();
  };

  const prevImage = () => {
    setCurrentIndex((prev) => (prev - 1 + items.length) % items.length);
    resetZoom();
  };

  const resetZoom = () => {
    setScale(1);
    setPosition({ x: 0, y: 0 });
  };

  const zoomIn = () => {
    setScale((prev) => Math.min(prev * 1.5, 4));
  };

  const zoomOut = () => {
    setScale((prev) => Math.max(prev / 1.5, 0.5));
  };

  const handleKeyDown = (e: KeyboardEvent) => {
    switch (e.key) {
      case 'Escape':
        onClose();
        break;
      case 'ArrowLeft':
        prevImage();
        break;
      case 'ArrowRight':
        nextImage();
        break;
      case '+':
        zoomIn();
        break;
      case '-':
        zoomOut();
        break;
    }
  };

  const handleDownload = () => {
    if (currentItem.type === 'image') {
      const link = document.createElement('a');
      link.href = currentItem.url;
      link.download = currentItem.alt || `image-${currentIndex + 1}`;
      link.click();
    }
  };

  useState(() => {
    const handleKeyDownEvent = (e: KeyboardEvent) => handleKeyDown(e);

    if (isOpen) {
      document.addEventListener('keydown', handleKeyDownEvent);
      document.body.style.overflow = 'hidden';
    } else {
      document.removeEventListener('keydown', handleKeyDownEvent);
      document.body.style.overflow = 'unset';
    }

    return () => {
      document.removeEventListener('keydown', handleKeyDownEvent);
      document.body.style.overflow = 'unset';
    };
  });

  if (!isOpen || !currentItem) return null;

  return (
    <AnimatePresence>
      <motion.div
        initial={{ opacity: 0 }}
        animate={{ opacity: 1 }}
        exit={{ opacity: 0 }}
        className="fixed inset-0 z-50 bg-black/95 backdrop-blur-sm"
        onClick={onClose}
      >
        {/* Header Controls */}
        <motion.div
          initial={{ opacity: 0, y: -20 }}
          animate={{ opacity: 1, y: 0 }}
          className="absolute top-0 left-0 right-0 z-60 p-4 bg-gradient-to-b from-black/50 to-transparent"
        >
          <div className="flex items-center justify-between text-white">
            <div className="flex items-center gap-4">
              <span className="text-sm">
                {currentIndex + 1} Ù…Ù† {items.length}
              </span>
              <span className="text-sm text-gray-300">
                {currentItem.alt}
              </span>
            </div>

            <div className="flex items-center gap-2">
              {currentItem.type === 'image' && (
                <>
                  <Button
                    variant="ghost"
                    size="sm"
                    onClick={(e) => {
                      e.stopPropagation();
                      zoomOut();
                    }}
                    className="text-white hover:bg-white/20"
                  >
                    <ZoomOut className="w-4 h-4" />
                  </Button>

                  <Button
                    variant="ghost"
                    size="sm"
                    onClick={(e) => {
                      e.stopPropagation();
                      zoomIn();
                    }}
                    className="text-white hover:bg-white/20"
                  >
                    <ZoomIn className="w-4 h-4" />
                  </Button>

                  <Button
                    variant="ghost"
