  type InsertWebProjectOrder,
  type WebOrder,
  type InsertWebOrder,
  type DesktopOrder,
  type InsertDesktopOrder,
  type GraphicsDesignRequest,
  type InsertGraphicsDesignRequest,
  users,
  contactSubmissions,
  portfolioItems,
  services,
  serviceSubcategories,
  testimonials,
  subscriptionPlans,
  userSubscriptions,
  serviceRequests,
  leads,
  contacts,
  accounts,
  opportunities,
  tasks,
  crmActivities,
  savedFilters,
  supportTickets,
  dealStages,
  ticketStatus,
  serviceAuditLog,
  mobileAppOrders,
  webProjectOrders,
  webOrders,
  desktopOrders,
  graphicsDesignRequests
} from "@shared/schema";
import { db } from "./db";
import { eq, and, desc, sql } from "drizzle-orm";
import bcrypt from "bcrypt";
import { IStorage } from "./storage";

export class DatabaseStorage implements IStorage {
  private async hashPassword(password: string): Promise<string> {
    return bcrypt.hash(password, 12);
  }

  async verifyPassword(password: string, hashedPassword: string): Promise<boolean> {
    return bcrypt.compare(password, hashedPassword);
  }

  // Audit logging helper method - Enhanced for Transaction Support
  private async createAuditLog(
    serviceId: string | null,
    operation: 'create' | 'update' | 'delete' | 'restore',
    oldValues: Record<string, any> | null,
    newValues: Record<string, any> | null,
    userId?: string,
    userName?: string,
    userRole?: string,
    reason?: string,
    ipAddress?: string,
    userAgent?: string,
    // Ø¥Ø¶Ø§ÙØ© Ù…Ø¹Ø§Ù…Ù„ Ø§Ù„Ù…Ø¹Ø§Ù…Ù„Ø© - Add transaction parameter
    tx?: typeof db
  ): Promise<void> {
    const dbInstance = tx || db;
    if (!dbInstance) return;

    try {
      // Calculate changed fields for update operations
      let changedFields: string[] = [];
      let riskLevel: 'low' | 'medium' | 'high' | 'critical' = 'low';

      if (operation === 'update' && oldValues && newValues) {
        changedFields = Object.keys(newValues).filter(key => 
          JSON.stringify(oldValues[key]) !== JSON.stringify(newValues[key])
        );

        // Determine risk level based on changed fields
        const criticalFields = ['id', 'title', 'category'];
        const highRiskFields = ['description', 'featured', 'isDeleted'];

        if (changedFields.some(field => criticalFields.includes(field))) {
          riskLevel = 'critical';
        } else if (changedFields.some(field => highRiskFields.includes(field))) {
          riskLevel = 'high';
        } else if (changedFields.length > 3) {
          riskLevel = 'medium';
        }
      } else if (operation === 'delete') {
        riskLevel = 'high';
      } else if (operation === 'create') {
        riskLevel = 'low';
      }

      await dbInstance.insert(serviceAuditLog).values({
        serviceId,
        operation,
        tableName: 'services',
        oldValues,
        newValues,
        changedFields,
        userId,
        userName,
        userRole,
        ipAddress,
        userAgent,
        reason,
        riskLevel
      });
    } catch (error) {
      console.error('Failed to create audit log:', error);
      // Don't throw here as audit logging should not break main operations
    }
  }

  // User Management
  async getUser(id: string): Promise<User | undefined> {
    if (!db) throw new Error("Database not available");
    const result = await db.select().from(users).where(eq(users.id, id)).limit(1);
    return result[0];
  }

  async getUserByUsername(username: string): Promise<User | undefined> {
    if (!db) throw new Error("Database not available");
    const result = await db.select().from(users).where(eq(users.username, username)).limit(1);
    return result[0];
  }

  async createUser(user: InsertUser): Promise<User> {
    if (!db) throw new Error("Database not available");
    const hashedPassword = await this.hashPassword(user.password);
    const result = await db.insert(users).values({
      ...user,
      password: hashedPassword
    }).returning();
    return result[0];
  }

  async updateUser(id: string, updates: Partial<User>): Promise<User> {
    if (!db) throw new Error("Database not available");
    if (updates.password) {
      updates.password = await this.hashPassword(updates.password);
    }
    const result = await db.update(users).set(updates).where(eq(users.id, id)).returning();
    return result[0];
