export default function TechBadge({ 
  technology, 
  index = 0, 
  variant = 'outline',
  size = 'md',
  interactive = true,
  className = ""
}: TechBadgeProps) {
  const icon = techIcons[technology] || 'âš™ï¸';

  const sizeClasses = {
    sm: 'text-xs px-2 py-1',
