                          <Badge className={`${getStatusColor(lead.lead_status)} text-white`}>
                            {lead.lead_status}
                          </Badge>
                        </div>
                        <div className="text-gray-600 mb-1">{lead.company}</div>
                        <div className="flex items-center gap-4 text-sm text-gray-500">
