              {index === breadcrumbs.length - 1 ? (
                <span className="text-brand-text-primary font-medium">
                  {crumb.label}
                </span>
              ) : (
                <Link 
                  href={crumb.href} 
                  className="hover:text-primary transition-colors duration-300"
                >
                  {crumb.label}
                </Link>
              )}

              {index < breadcrumbs.length - 1 && (
                <ChevronRight 
                  className={cn(
                    "w-4 h-4",
                    dir === 'rtl' && "rotate-180"
                  )} 
                />
              )}
            </div>
          ))}
        </div>
      </div>
    </motion.nav>
  );
}

