  id: string;
  name: string;
  columns: string[];
  sorts: TableSort[];
  filters: TableFilter[];
  pageSize: number;
}

export interface TableControllerProps {
  // Data fetching
  endpoint: string;
  queryKey: string[];

  // Table configuration
  columns: TableColumn[];
  defaultPageSize?: number;
  defaultSort?: TableSort[];

  // Features
  enableExport?: boolean;
  enableSavedViews?: boolean;
  enableColumnToggle?: boolean;

  // Styling
