import React, { useState, useEffect } from 'react';
import { Card } from './base/Card';
import { Badge } from './base/Badge';

interface TimelineActivity {
  id: string;
  type: 'call' | 'meeting' | 'task' | 'message' | 'note' | 'attachment';
  title: string;
  subject?: string;
  description?: string;
  actorId?: string;
  outcome?: string;
  durationSec?: number;
  attachments?: Array<{
    id: string;
    name: string;
    url: string;
    size: number;
    mimeType: string;
  }>;
  dueAt?: string;
  reminderAt?: string;
  completedAt?: string;
  isCompleted: boolean;
  createdAt: string;
  updatedAt: string;
}

interface CrmTimelineProps {
  entityType: 'contact' | 'account' | 'deal' | 'lead';
  entityId: string;
  onAddActivity?: () => void;
}

export const CrmTimeline: React.FC<CrmTimelineProps> = ({
  entityType,
  entityId,
  onAddActivity
}) => {
  const [activities, setActivities] = useState<TimelineActivity[]>([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    fetchActivities();
  }, [entityType, entityId]);

  const fetchActivities = async () => {
    try {
      setLoading(true);
      const response = await fetch(`/api/crm/activities/${entityType}/${entityId}`);
      if (response.ok) {
        const data = await response.json();
        setActivities(data);
      }
    } catch (error) {
      console.error('Error fetching activities:', error);
    } finally {
      setLoading(false);
    }
  };

  const getActivityIcon = (type: string) => {
    switch (type) {
      case 'call': return 'fas fa-phone';
      case 'meeting': return 'fas fa-calendar';
      case 'task': return 'fas fa-tasks';
      case 'message': return 'fas fa-envelope';
      case 'note': return 'fas fa-sticky-note';
      case 'attachment': return 'fas fa-paperclip';
      default: return 'fas fa-circle';
    }
  };

